import React, { useState } from "react";

function Counter() {
  const [car, setCar] = useState(0);
  let inc = () => {
    setCar(car + 1);
  };
  let dec = () => {
    if(car>0){
    setCar(car - 1);
  }};
  let clear = () => {
    setCar(0);
  };
  return (
    <div>
      <button
        style={{
          background: "White",
          border:'0',
          borderRadius: "20%",
          width: "70px",
          height: "60px",
          margin: "5px",
        }}
      >
        <h1>{car}</h1>
      </button>
      <br />
      <button
        style={{
          background: "Blue",
          border:'0',
          borderRadius: "20%",
          width: "70px",
          height: "60px",
          margin: "5px",
        }}
        onClick={inc}
      >
        <h1>+</h1>
      </button>
      <button
        style={{
          background: "Red",
          border:'0',
          borderRadius: "20%",
          width: "70px",
          height: "60px",
          margin: "5px",
        }}
        onClick={dec}
      >
        <h1>-</h1>
      </button>
      <button
        onClick={clear}
        style={{
          background: "Orange",
          border:'0',
          borderRadius: "20%",
          width: "70px",
          height: "60px",
          margin: "5px",
        }}
      >
        <h1>CE</h1>
      </button>
    </div>
  );
}

export default Counter;
