import React, { useState } from "react";

function Counter() {
  const [car, setCar] = useState(0);
  let inc = () => {
    setCar(car + 1);
  };
  let dec = () => {
    setCar(car - 1);
  };
  let clear = () => {
    setCar(0);
  };
  return (
    <div>
      <button
        style={{
            background: "White",
            borderRadius: "20%",
            width: "70px",
            height: "60px",
            margin: "5px",
          }}
      >
        <h1>{car}</h1>
      </button>
      <br />
      <button
       style={{
        background: "Blue",
        borderRadius: "20%",
        width: "70px",
        height: "60px",
        margin: "5px",
      }}
        onClick={inc}
      >
        <h1>+</h1>
      </button>
      <button
       style={{
        background: "Red",
        borderRadius: "20%",
        width: "70px",
        height: "60px",
        margin: "5px",
      }}
        onClick={dec}
      >
        <h1>-</h1>
      </button>
      <button
        onClick={clear}
        style={{
          background: "Orange",
          borderRadius: "20%",
          width: "70px",
          height: "60px",
          margin: "5px",
        }}
      >
        <h1>Clear</h1>
      </button>
    </div>
  );
}

export default Counter;
