import React from 'react';
import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const ref1 = React.createRef();
  const ref2 = React.createRef();
  const [value, setvalue] = useState(0);
  function add() {
    const add = parseInt(ref1.current.value) + parseInt(ref2.current.value);
    setvalue(add);
  }
  function sub() {
    const sub = parseInt(ref1.current.value) - parseInt(ref2.current.value);
    setvalue(sub);
  }
  function mul() {
    const mul = parseInt(ref1.current.value) * parseInt(ref2.current.value);
    setvalue(mul);
  }
  function div() {
    const div = parseInt(ref1.current.value) / parseInt(ref2.current.value);
    setvalue(div);
  }

  return (
    <div className="App">
      <input type="number" placeholder='number 1' id='num1' ref={ref1} />
      <input type="number" placeholder='number 2' id='num2' ref={ref2} />
      <p>The value = {value}</p>
      <button onClick={add} style={{ width: '50px', height: '50px', margin: '5px' }}><h3>+</h3></button>
      <button onClick={sub} style={{ width: '50px', height: '50px', margin: '5px' }}><h3>-</h3></button>
      <button onClick={mul} style={{ width: '50px', height: '50px', margin: '5px' }}><h3>*</h3></button>
      <button onClick={div} style={{ width: '50px', height: '50px', margin: '5px' }}><h3>/</h3></button>
    </div>
  );
}

export default App;
