import React, { useState } from "react";
import "./Calculator.css";
// import image from "./unnamed.jpg";
function Counter() {
  const [exp, setexp] = useState("");
  const [bg, setbg] = useState({ theme: false });

  const createExpr = (value) => {
    setexp((prev) => {
      let returnValue = "";
      if (
        value === "*" ||
        value === "+" ||
        value === "-" ||
        value === "/" ||
        value === "%"
      ) {
        if (
          prev.charAt(prev.length - 1) === "%" ||
          prev.charAt(prev.length - 1) === "+" ||
          prev.charAt(prev.length - 1) === "-" ||
          prev.charAt(prev.length - 1) === "*" ||
          prev.charAt(prev.length - 1) === "/"
        ) {
          returnValue = prev.substring(0, prev.length - 1) + value;
        } else {
          returnValue = prev + value;
        }
      } else {
        returnValue = prev + value;
      }
      return returnValue;
    });
  };
  const clearAll = () => {
    setexp("");
  };
  const changeBg = () => {
    setbg({ theme: !bg.theme });
  };
  const removeLastEle = () => {
    setexp((prev) => prev.substring(0, prev.length - 1));
  };
  const calculateExp = () => {
    setexp((prev) => {
      if (prev === "1+") {
        return "NEVER SETTLE";
      } else if (
        prev.length > 0 &&
        prev.charAt(prev.length - 1) !== "%" &&
        prev.charAt(prev.length - 1) !== "+" &&
        prev.charAt(prev.length - 1) !== "-" &&
        prev.charAt(prev.length - 1) !== "*" &&
        prev.charAt(prev.length - 1) !== "/"
      ) 
      {
        let ans = eval(prev);
        return ans.toString();
      } 
      else {
        return prev;
      }
    });
  };
  return (
    <div
      id="Border"
      style={bg.theme ? {backgroundColor:"black"} : {}}
    >
      <div id="Ap">
        <div className="result">
          <h1>{exp}</h1>
        </div>
      </div>
      <button
        id="Bt"
        style={
          bg.theme
            ? { backgroundColor: "white", color: "black" }
            : { backgroundColor: "black", color: "white" }
        }
        onClick={changeBg}
      >
        <h1>COLOR</h1>
      </button>
      <br />
      {/* <button id="App4" onClick={() => createExpr("!")}>
        <h1>!</h1>
      </button> */}
      <button id="App" onClick={clearAll}>
        <h1>CE</h1>
      </button>
      <button id="App1" onClick={() => createExpr("%")}>
        <h1>%</h1>
      </button>
      <button id="App3" onClick={removeLastEle}>
        <h1>{"<-"}</h1>
      </button>
      <button id="App1" onClick={() => createExpr("/")}>
        <h1>/</h1>
      </button><br />
      {/* <button id="App4" onClick={() => createExpr("^")}>
        <h1>^</h1>
      </button> */}
      <button id="App2" onClick={() => createExpr("7")}>
        <h1>7</h1>
      </button>
      <button id="App2" onClick={() => createExpr("8")}>
        <h1>8</h1>
      </button>
      <button id="App2" onClick={() => createExpr("9")}>
        <h1>9</h1>
      </button>
      <button id="App1" onClick={() => createExpr("*")}>
        <h1>X</h1>
      </button>
      <br />
      {/* <button id="App4" onClick={() => createExpr("√")}>
        <h1>√</h1>
      </button> */}
      <button id="App2" onClick={() => createExpr("4")}>
        <h1>4</h1>
      </button>
      <button id="App2" onClick={() => createExpr("5")}>
        <h1>5</h1>
      </button>
      <button id="App2" onClick={() => createExpr("6")}>
        <h1>6</h1>
      </button>
      <button id="App1" onClick={() => createExpr("-")}>
        <h1>-</h1>
      </button>
      <br />
      {/* <button id="App4" onClick={() => createExpr("π")}>
        <h1>π</h1>
      </button> */}
      <button id="App2" onClick={() => createExpr("1")}>
        <h1>1</h1>
      </button>
      <button id="App2" onClick={() => createExpr("2")}>
        <h1>2</h1>
      </button>
      <button id="App2" onClick={() => createExpr("3")}>
        <h1>3</h1>
      </button>
      <button id="App1" onClick={() => createExpr("+")}>
        <h1>+</h1>
      </button>
      <br />
      <button id="App4" onClick={() => createExpr("e")}>
        <h1>e</h1>
      </button>
      <button id="App2" onClick={() => createExpr("00")}>
        <h1>00</h1>
      </button>
      <button id="App2" onClick={() => createExpr("0")}>
        <h1>0</h1>
      </button>
      <button id="App2" onClick={() => createExpr(".")}>
        <h1>.</h1>
      </button>
      <button id="App" onClick={calculateExp}>
        <h1>=</h1>
      </button>
    </div>
  );
}

export default Counter;
