import React, { useCallback, useState } from "react";
function Login() {
  const [verify, setverify] = useState({
    email: "",
    pass: "",
    showpass: f,
  });
  const verfication = useCallback((e) => {
    setverify((prev) => {
      return {
        ...prev,
        [e.target.email]: e.target.value,
        [e.target.email]: e.target.value,
      };
    });
  }, []);
  const show = useCallback((e) => {
    setverify((prev) => {
      return { ...prev, [e.target.name]: e.target.checked };
    });
  }, []);
  const submitHandler = useCallback(
    (e) => {
      e.preventDefault();
      if (verify.email === "") {
        console.log("y bro");
      }
    },
    [verify]
  );
  return (
    <div>
      <form onSubmit={submitHandler}>
        <label for="email"> EMAIL : </label>
        <input type="email" id="email" onChange={verfication} /> <br />
        <label for="password"> PASSWORD : </label>
        <input type="password" id="password" onChange={verfication} /> <br />
        <input type="checkbox" id="show" onChange={show} />
        <label for="show"> Show pass </label>
        <br />
        <button type="submit">Login</button>
        <button type="reset">Reset</button>
      </form>
    </div>
  );
}

export default Login;
