import React, { useState } from "react";
import "./calculator.css";
function Calculator() {
  const ref1 = React.createRef();
  const ref2 = React.createRef();
  const [value, setvalue] = useState(0);
  function add() {
    const add = parseInt(ref1.current.value) + parseInt(ref2.current.value);
    setvalue(add);
  }
  function sub() {
    const sub = parseInt(ref1.current.value) - parseInt(ref2.current.value);
    setvalue(sub);
  }
  function mul() {
    const mul = parseInt(ref1.current.value) * parseInt(ref2.current.value);
    setvalue(mul);
  }
  function div() {
    const div = parseInt(ref1.current.value) / parseInt(ref2.current.value);
    setvalue(div);
  }
  function clear() {
    ref1.current.value = "";
    ref2.current.value = "";
    setvalue(0);
  }

  return (
    <div className="App">
      <input
        style={{
          background: "lightGreen",
          borderRadius: "50%",
          height: "50px",
          margin: "5px",
          textAlign: "center",
          fontSize: "30px",
        }}
        type="number"
        placeholder="0"
        // id="num1"
        ref={ref1}
      />
      <input
        style={{
          background: "lightGreen",
          borderRadius: "50%",
          height: "50px",
          margin: "5px",
          textAlign: "center",
          fontSize: "30px",
        }}
        type="number"
        placeholder="0"
        // id="num2"
        ref={ref2}
      />
      <br />
      <h1 style={{ color: "red" }}>||</h1>
      <br />
      <button
        style={{
          background: "lightBlue",
          borderRadius: "50%",
          height: "50px",
          margin: "5px",
          textAlign: "center",
          fontSize: "30px",
        }}
      >
        {value}
      </button>
      <br />
      <button
        onClick={add}
        style={{
          background: "Orange",
          borderRadius: "20%",
          width: "50px",
          height: "60px",
          margin: "5px",
        }}
      >
        <h2>+</h2>
      </button>
      <button
        onClick={sub}
        style={{
          background: "Orange",
          borderRadius: "20%",
          width: "50px",
          height: "60px",
          margin: "5px",
        }}
      >
        <h2>-</h2>
      </button>
      <button
        onClick={mul}
        style={{
          background: "Orange",
          borderRadius: "20%",
          width: "50px",
          height: "60px",
          margin: "5px",
        }}
      >
        <h2>X</h2>
      </button>
      <button
        onClick={div}
        style={{
          background: "Orange",
          borderRadius: "20%",
          width: "50px",
          height: "60px",
          margin: "5px",
        }}
      >
        <h2>/</h2>
      </button>
      <button
        onClick={clear}
        style={{
          background: "Orange",
          borderRadius: "20%",
          width: "70px",
          height: "60px",
          margin: "5px",
        }}
      >
        <h2>Clear</h2>
      </button>
    </div>
  );
}

export default Calculator;
